

var clickX = new Array();
var clickY = new Array();
var clickDrag = new Array();
var paint;
console.log("READ");
function prepareCanvas(){
  console.log("WHAT");
  context = document.getElementById('canvasDiv').getContext("2d");
  canvas = document.getElementById('canvasDiv');
  canvas.width = window.innerWidth;
  canvas.height = window.innerHeight *.6;

  $('#canvasDiv').mousedown(function(e){
  var mouseX = e.pageX - this.offsetLeft;
  var mouseY = e.pageY - this.offsetTop;
  
  paint = true;
  addClick(e.pageX - this.offsetLeft, e.pageY - this.offsetTop);
  redraw();
  });

  $('#canvasDiv').mousemove(function(e){
    if(paint){
      addClick(e.pageX - this.offsetLeft, e.pageY - this.offsetTop, true);
      redraw();
    }
  });

  $('#canvasDiv').mouseup(function(e){
    paint = false;
  });

  $('#canvasDiv').mouseleave(function(e){
    paint = false;
  }); 
  $('#clearCanvas').mousedown(function(e){
    console.log("CLEAR");
    clickX = new Array();
    clickY = new Array();
    clickDrag = new Array();
    context.clearRect(0, 0, canvas.width, canvas.height);
  });
}

function addClick(x, y, dragging)
{
  clickX.push(x);
  clickY.push(y);
  clickDrag.push(dragging);
}



function redraw(){
  context.clearRect(0, 0, context.canvas.width, context.canvas.height); // Clears the canvas
  
  context.strokeStyle = "#df4b26";
  context.lineJoin = "round";
  context.lineWidth = 5;
			
  for(var i=0; i < clickX.length; i++) {		
    context.beginPath();
    if(clickDrag[i] && i){
      context.moveTo(clickX[i-1], clickY[i-1]);
     }else{
       context.moveTo(clickX[i]-1, clickY[i]);
     }
     context.lineTo(clickX[i], clickY[i]);
     context.closePath();
     context.stroke();
  }
}